import React,{useState,useEffect} from 'react';
import Card from './Card';
import Columnrow from './Columnrow';

function Cardgroup() {

    const [card, setCard] = useState([]);
    useEffect(() => {
        fetch(`https://newsapi.org/v2/top-headlines?sources=bbc-news&apiKey=bb5b1c906cb34c1d90fe13b072449dee`).then(res => {
            return res.json()
        }).then(ans => {
            console.log('called')
            console.log(ans)
            setCard(ans.articles.map((item, index) => {
                if(index <= 4)
                    return index === 0 ?  <div className="col col-md-6 col-sm-12"><Card key={index} heading={item.title} imageUrl={item.urlToImage} content={item.description}/></div> : <Columnrow key={index} heading={item.title} imageUrl={item.urlToImage} content={item.description}/>
            }).filter(item => item))
        }).catch(err => {
            throw err
        })
    }, [])
    return (
        <React.Fragment>
            <h1 className="news">BBC-NEWS</h1>
            <div class="row">
                {card}
            </div>
        </React.Fragment>
    )
}

export default Cardgroup;
