import React,{useState} from 'react';

function Dropdown() {
const [name,setname] = useState("IND")
const UK =()=>{
    setname("UK")
}
const US =()=>{
    setname("US")
}
const IND =()=>{
    setname("IND")
}
const CN =()=>{
    setname("CN")
}
    return (
        <div class="dropdown">

        <button class="btn btn-outline-light dropdown-toggle" type="button" id="dropdownMenu1" data-toggle="dropdown"
                aria-haspopup="true" aria-expanded="false">{name}</button>

        <div class="dropdown-menu dropdown-primary">
                <a class="dropdown-item" onClick={UK} href="#">UK</a>
                <a class="dropdown-item" onClick={US} href="#">US</a>
                <a class="dropdown-item" onClick={IND} href="#">IND</a>
                <a class="dropdown-item" onClick={CN} href="#">CN</a>
            </div>
        </div>
    )
}
export default Dropdown;
