import React,{useState,useEffect} from 'react';
import Card from './Card';
import ColumnRow from './columnrow';

function Cardgroup() {

    const [card, setCard] = useState([]);
    useEffect(() => {
        fetch(`https://newsapi.org/v2/top-headlines?sources=bbc-news&apiKey=bb5b1c906cb34c1d90fe13b072449dee`).then(res => {
            console.log('called')
            return res.json()
        }).then(ans => {
            console.log(ans)
            setCard(ans.articles.map((item, index) => {
                if(index <= 4)
                    return index === 0 ? <Card key={index} heading={item.title} imageUrl={item.urlToImage} content={item.description}/> : <ColumnRow key={index} heading={item.title} imageUrl={item.urlToImage} content={item.description}/>
            }))
        }).catch(err => {
            throw err
        })
    }, [])

    return (
        <React.Fragment>
            <h1 className="news">BBC-NEWS</h1>
            <div class="row">
                <div className="col col-md-6 col-sm-12">
                    <Card />
                </div>
                {card}
            </div>
        </React.Fragment>
    )
}

export default Cardgroup;
