import React, { useState } from 'react';

function Dropdown() {
    const [name, setName] = useState("IND")
    function handleClick(e){
        setName(e.target.innerText)
    }
    // const UK =()=>{
    //     setName("UK")
    // }
    // const US =()=>{
    //     setName("US")
    // }
    // const IND =()=>{
    //     setName("IND")
    // }
    // const CN =()=>{
    //     setName("CN")
    // }
    return (
        <div class="dropdown">
            <button class="btn btn-outline-light dropdown-toggle" type="button" id="dropdownMenu1" data-toggle="dropdown"
                aria-haspopup="true" aria-expanded="false">{name}</button>
            <div class="dropdown-menu dropdown-primary">
                <a class="dropdown-item" onClick={handleClick} href="#">UK</a>
                <a class="dropdown-item" onClick={handleClick} href="#">US</a>
                <a class="dropdown-item" onClick={handleClick} href="#">IND</a>
                <a class="dropdown-item" onClick={handleClick} href="#">CN</a>
            </div>
        </div>
    )
}
export default Dropdown;
