import React,{useState,useEffect} from 'react';
import Nav from './component/navbar/Nav';
import Footer from './component/footer/Footer';
import Carousel from './component/body/carousel/Carousel';
import Cardgroup from './component/body/multiDiv/Cardgroup';

function App() {
  const [region,setRegion] = useState('in');
  function getChangedRegion(data){
    console.log(data)
    setRegion(data)
  }
  return (
    <div>
      <Nav getRegion={getChangedRegion}/>
      <Carousel region={region}/>
      <div className="container">
      <Cardgroup region={region}  catagory={'general'} name={'GENERAL-NEWS'}/><br/>
      <Cardgroup region={region} catagory={ 'business'} name={'BUSINESS-NEWS'} /><br/>
      <Cardgroup region={region}  catagory={'entertainment'} name={'ENTERTAINMENT-NEWS'}/><br/>
      <Cardgroup region={region}  catagory={'health'} name={'HEALTH-NEWS'}/><br/>
      <Cardgroup region={region}  catagory={'science'} name={'SCIENCE-NEWS'}/><br/>
      <Cardgroup region={region}  catagory={'sports'} name={'SPORTS-NEWS'}/><br/>
      <Cardgroup region={region}  catagory={'technology'} name={'TECHNOLOGY-NEWS'}/><br/>
      </div>
      <Footer/>
    </div>
  );
}

export default App;
