import React,{useState} from 'react';
import CarouselImages from './Carouselimages';

function CarouselImage() {
    const [headlines,setHeadlines] = useState('');
    fetch("https://newsapi.org/v2/top-headlines?country=us&apiKey=bb5b1c906cb34c1d90fe13b072449dee").then(res =>{
        return res.json()
    }).then(ans =>{
        //console.log(ans.articles)
        const comp = ans.articles.map((item,index) => {
            if(index > 2){
                return null
            }
            if(index === 0) 
                return <CarouselImages active={true} heading={item.title} imageUrl={item.urlToImage} />
            return <CarouselImages heading={item.title} imageUrl={item.urlToImage}/>
        }).filter(item => item)
        setHeadlines(comp)
    }).catch(err =>{
        throw err
    })
    return (
        <div class="carousel-inner">
            {headlines}
        </div >
    )
}

export default CarouselImage;
